import React from 'react';
import { CountdownCircleTimer } from 'react-countdown-circle-timer';

function CountdownTimer() {

    const [key, setKey] = useState(0);
  
    const handleComplete = () => {
      // This function will be called when the countdown completes
      // Update the key to trigger a reset and restart the countdown
      setKey(prevKey => prevKey + 1);
    };
  

  return (
    <div className="countdown-timer">
      <CountdownCircleTimer
        isPlaying
        duration={60} // Countdown duration in seconds
        colors={[['#3DC6C1']]} // Use a single color
        size={45}
        strokeWidth={5}
        onComplete={handleComplete} // Callback when countdown completes

      >
        {({ remainingTime }) => (
          <div className="timer">
            <div  style={{
                fontSize: '20px',
                fontWeight: 'bold',
                fontFamily: 'Arial, sans-serif',
              }} className="text">{remainingTime}</div>
          </div>
        )}
      </CountdownCircleTimer>
    </div>
  );
}

export default CountdownTimer;
